const initialState = { anchor: 'left',
    marcaArreglo: [],
    open: false,
    clave: '',  
    descrip: '',
    usuario: ''
 };


export function marca(state = initialState, action) {
    switch (action.type) {
        case 'FETECHED_ALL_MARCA':
            return {
            ...state,
            marca: action.marca
            };
        case 'MARCA_DETAIL':
            return {
                ...state,
                marca: action.clave,
                descrip: action.descrip,
                usuario: action.usuario
            };
        case "USER_UPDATED":
            return state;
        case "HANDLE_ON_CHANGE":
            return {
                ...state,
                [action.props]: action.value
            };    
        default:
            return state
    }
  }
