import { userService } from '../_services';
import { history } from '../_helpers';

export const Action = {
    getMarca,
    getMarcaById,
    onChangeProps,
    editMarcaInfo,
    createMarca,
    deleteMarcaById
};

function getMarca(){
    return dispatch => {
        let apiEndpoint = 'Marcas';
        userService.get(apiEndpoint)
        .then((response)=>{
            console.log(response);
            dispatch(changeVendorsList(response.data.data));
        }).catch((err)=>{
            console.log("Error");
            console.log(err);
        })
    };
}

function createMarca(payload){
    return dispatch => {
        let apiEndpoint = 'Marcas/';
        userService.post(apiEndpoint, payload)
        .then((response)=>{
            dispatch(createUserInfo());
            history.push('/marca');
        }) 
    }
}

function getMarcaById(id){

    return dispatch => {
        let apiEndpoint = 'Marcas/'+ id;
        userService.get(apiEndpoint)
        .then((response)=>{
            dispatch(editVendorsDetails(response.data.data));
        })
    };
}

function onChangeProps(props, event){
    return dispatch =>{
        dispatch(handleOnChangeProps(props, event.target.value));
    }
}

function editMarcaInfo(id, payload){
    return dispatch => {
        let apiEndpoint = 'Marcas/'+ id;
        userService.put(apiEndpoint, payload)
        .then((response)=>{
            dispatch(updatedUserInfo());
            history.push('/marca');
        }) 
    }
}

function deleteMarcaById(id){
    return dispatch => {
        let apiEndpoint = 'Marcas/'+ id;
        userService.deleteDetail(apiEndpoint)
        .then((response)=>{
            dispatch(deleteMarcasDetails());
            dispatch(marcaAction.getMarca());
        })
    };
}

export function changeMarcasList(marca){
    return{
        type: "FETECHED_ALL_VENDOR",
        marca: marca
    }
}

export function handleOnChangeProps(props, value){
    return{
        type: "HANDLE_ON_CHANGE",
        props: props,
        value: value
    }
}

export function editMarcasDetails(vendor){
    return{
        type: "MARCA_DETAIL",
        id: marca._id,
        name: vendor.name,
        mobile: vendor.mobile,
        phone_number: vendor.phone_number,
        address: vendor.address
    }
}

export function updatedUserInfo(){
    return{
        type: "USER_UPDATED"
    }
}

export function createUserInfo(){
    return{
        type: "USER_CREATED_SUCCESSFULLY"
    }
}

export function deleteVendorsDetails(){
    return{
        type: "DELETED_VENDOR_DETAILS"
    }
}
